import React from 'react';

const Light = (props) => {

    let el = document.getElementById('item');

    if (el.className === 'active'){
        el.className = 'inactive';
    } else {
        el.className = 'active';
    }

    return (
      <div>
          <div id='light' className='bg-dark d-block mx-auto my-3'>
              <div className="red-light bg-danger rounded-circle d-block mx-auto"></div>
              <div className="green-light bg-success rounded-circle d-block mx-auto"></div>
          </div>
      </div>
    );
}

export default Light
