import React from 'react';
import {connect} from 'react-redux';
import {go, stop} from '../actions';

const Controller = ({dispatch, intersection}) => {

    const northSouthGo = {
        north: 'green',
        south: 'green',
        east: 'red',
        west: 'red'
    }

    const eastWestGo = {
        north: 'red',
        south: 'red',
        east: 'green',
        west: 'green'
    }


    const handleGo = (e) => {
        const direction = e.target.innerHTML
        let payload = {}
        payload[direction] = 'green'
        if (direction === 'north' || direction === 'south') {
            payload = northSouthGo
        } else if (direction === 'east' || direction === 'west') {
            payload = eastWestGo
        }
        dispatch(go(payload))
    }

    const handleStop = (e) => {
        const direction = e.target.innerHTML
        let payload = {}
        payload[direction] = 'red'
        if (direction === 'east' || direction === 'west') {
            payload = northSouthGo
        } else if (direction === 'north' || direction === 'south') {
            payload = eastWestGo
        }
        dispatch(stop(payload))
    }


    return (
        <div className='d-flex justify-content-center text-light'>
            <div className="flex-item w-25 m-5 text-capitalize">
                <h2>Which way you want to go/stop?</h2>
                <div className="row">
                    <button
                        className="btn btn-green col border m-1 text-light"
                        onClick={handleGo}
                    >
                        north
                    </button>
                    <button
                        className="btn btn-red col border m-1 text-light"
                        onClick={handleStop}
                    >
                        north
                    </button>
                </div>
                <div className="row">
                    <button
                        className="btn btn-green col border m-1 text-light"
                        onClick={handleGo}
                    >
                        east
                    </button>
                    <button
                        className="btn btn-red col border m-1 text-light"
                        onClick={handleStop}
                    >
                        east
                    </button>
                </div>
                <div className="row">
                    <button
                        className="btn btn-green col border m-1 text-light"
                        onClick={handleGo}
                    >
                        south
                    </button>
                    <button
                        className="btn btn-red col border m-1 text-light"
                        onClick={handleStop}
                    >
                        south
                    </button>
                </div>
                <div className="row">
                    <button
                        className="btn btn-green col border m-1 text-light"
                        onClick={handleGo}
                    >
                        west
                    </button>
                    <button
                        className="btn btn-red col border m-1 text-light"
                        onClick={handleStop}
                    >
                        west
                    </button>
                </div>
            </div>
            <div className="compass-group flex-item">
                <h1>North: {intersection.north}</h1>
                <div className="d-flex justify-content-around align-items-center">
                    <h1>West: {intersection.west}</h1>
                    <img className='compass' src='/img/compass.png' alt='compass'/>
                    <h1>East: {intersection.east}</h1>
                </div>
                <h1>South: {intersection.south}</h1>
            </div>
        </div>
    )
}

const mapStateToProps = (state) => ({intersection: state.intersection})


export default connect(mapStateToProps)(Controller)
