import React from 'react';
import {connect} from 'react-redux';
import {go, stop} from '../actions';
import intersection from '../reducers/intersection';

const Controller = ({dispatch, intersection}) => {

    const handleGo = (e) => {
        const direction = e.target.innerHTML
        const payload = {}
        payload[direction] = 'green'
        dispatch(go())
    }

    const handleStop = (e) => {
        const direction = e.target.innerHTML
        const payload = {}
        payload[direction] = 'red'
        dispatch(stop())
    }


    return (
        <div>
            <h1>N: {intersection.north}</h1>
            <h1>E: {intersection.east}</h1>
            <h1>S: {intersection.south}</h1>
            <h1>W: {intersection.west}</h1>
            <div className="row d-block mx-auto">
                <button
                    className="btn btn-success col-3 border"
                    onClick={handleGo}
                >
                    north
                </button>
                <button
                    className="btn btn-danger col-3 border"
                    onClick={handleStop}
                >
                    north
                </button>
            </div>
            <div className="row d-block mx-auto">
                <button
                    className="btn btn-success col-3 border"
                    onClick={handleGo}
                >
                    east
                </button>
                <button
                    className="btn btn-danger col-3 border"
                    onClick={handleStop}
                >
                    east
                </button>
            </div>
            <div className="row d-block mx-auto">
                <button
                    className="btn btn-success col-3 border"
                    onClick={handleGo}
                >
                    south
                </button>
                <button
                    className="btn btn-danger col-3 border"
                    onClick={handleStop}
                >
                    south
                </button>
            </div>
            <div className="row d-block mx-auto">
                <button
                    className="btn btn-success col-3 border"
                    onClick={handleGo}
                >
                    west
                </button>
                <button
                    className="btn btn-danger col-3 border"
                    onClick={handleStop}
                >
                    west
                </button>
            </div>

        </div>
    )
}

const mapStateToProps = (state) => {
    return ({intersection: state.intersection})
}

export default connect(mapStateToProps)(Controller)
