const intersection = (state = defaultState, action) => {
    switch (action.type) {
        case 'GO':
            break;
        case 'STOP':
            break;
        default:
            return state
    }
}

const defaultState = {
    north: 'red',
    east: 'red',
    south: 'red',
    west: 'red'
}

export default intersection
