import React from 'react';
import {connect} from 'react-redux';
import {go, stop} from '../actions';

const Controller = ({dispatch, intersection}) => {

    const northSouthGo = {
        north: 'green',
        south: 'green',
        east: 'red',
        west: 'red'
    }

    const eastWestGo = {
        north: 'green',
        south: 'green',
        east: 'red',
        west: 'red'
    }


    const handleGo = (e) => {
        const direction = e.target.innerHTML
        let payload = {}
        payload[direction] = 'green'
        if (direction === 'north' || direction === 'south'){
            payload = northSouthGo
        }
        if (direction === 'east' || direction === 'west'){
            payload = eastWestGo
        }
        dispatch(go(payload))
    }

    const handleStop = (e) => {
        const direction = e.target.innerHTML
        let payload = {}
        payload[direction] = 'red'
        if (direction === 'east' && payload[direction] === 'red' ||
            direction === 'west' && payload[direction] === 'red'
        ){
            payload = {
                north: 'green',
                south: 'green',
                east: 'red',
                west: 'red'
            }
        } else
        if (direction === 'north' && payload[direction] === 'red' ||
            direction === 'south' && payload[direction] === 'red'
        ){
            payload = {
                north: 'red',
                south: 'red',
                east: 'green',
                west: 'green'
            }
        }
        dispatch(stop(payload))
    }


    return (
        <div>
            <h1>N: {intersection.north}</h1>
            <h1>E: {intersection.east}</h1>
            <h1>S: {intersection.south}</h1>
            <h1>W: {intersection.west}</h1>
            <div className="row d-block mx-auto">
                <button
                    className="btn btn-success col-3 border"
                    onClick={handleGo}
                >
                    north
                </button>
                <button
                    className="btn btn-danger col-3 border"
                    onClick={handleStop}
                >
                    north
                </button>
            </div>
            <div className="row d-block mx-auto">
                <button
                    className="btn btn-success col-3 border"
                    onClick={handleGo}
                >
                    east
                </button>
                <button
                    className="btn btn-danger col-3 border"
                    onClick={handleStop}
                >
                    east
                </button>
            </div>
            <div className="row d-block mx-auto">
                <button
                    className="btn btn-success col-3 border"
                    onClick={handleGo}
                >
                    south
                </button>
                <button
                    className="btn btn-danger col-3 border"
                    onClick={handleStop}
                >
                    south
                </button>
            </div>
            <div className="row d-block mx-auto">
                <button
                    className="btn btn-success col-3 border"
                    onClick={handleGo}
                >
                    west
                </button>
                <button
                    className="btn btn-danger col-3 border"
                    onClick={handleStop}
                >
                    west
                </button>
            </div>

        </div>
    )
}

const mapStateToProps = (state) => ({intersection: state.intersection})


export default connect(mapStateToProps)(Controller)
