import React from 'react';

const Light = (props) => {

    return (
        <div>
            <div id='light' className='bg-dark mx-auto'>
                <div className={props.color === 'red' ? 'active': 'inactive'}>
                    <div className="red-light bg-danger rounded-circle d-block mx-auto"></div>
                </div>
                <div className={props.color === 'green' ? 'active': 'inactive'}>
                    <div className="green-light bg-success rounded-circle d-block mx-auto"></div>
                </div>
            </div>
        </div>
    );
}

export default Light
